65.query:
timeseries availability = sum(dt.host.availability, scalar: true, filter: { dt.entity.host == "HOST-xxxx" }), by: { availability.state, dt.metrics.source, dt.host_group.id, host.name, dt.entity.host }
| summarize { availability = sum(availability) }, by: { availability.state, timeframe, interval }
| sort availability desc
| join [ timeseries availability = sum(dt.host.availability, scalar: true, filter: { dt.entity.host == "HOST-xxxx" }), by: { availability.state, dt.metrics.source, dt.host_group.id, host.name, dt.entity.host }
       | summarize { availability = sum(availability) }, by: { timeframe, interval } ], on: { interval }, fields: { operand = availability }
| fieldsAdd expression = availability / operand
| fieldsRemove operand, availability
| fieldsAdd expression = expression * 100
